let value = arguments[0].value;
for (let entity of value.entities) {
    let roll = value.attacks[entity.id];
    if (roll.total >= entity.actor.system.attributes.ac.value || roll.total == 20) {
        if (value.attack.rollDamage) {
            value.attack.rollDamage({critical: roll.total == 20, options: { flavor: "Damage for " + entity.name}});
        }
    }
}

/*
So if you forget to set the Tile to active while you're creating them
or if you want to reset the scene you can use this macro to re-activate
all the Active Tiles.
*/
let count = 0;
for (let tile of canvas.scene.tiles){
  if (!getProperty(tile, "flags.monks-active-tiles")?.active){
    await tile.setFlag('monks-active-tiles', 'active', true);
    count++;
  }
}
ui.notifications.info(`Activated ${count} tiles`);
